settings.custom_liquid:
{% assign scheme = settings.color_scheme_1 %}

<h2 style="text-align: center; color: rgb({{ scheme.text }}); font-family: var(--font-heading-family); font-size: {{ settings.type_header_font_size }}px;">Recensioni di Google</h2>
<p style="text-align: center; font-size: {{ settings.type_body_font_size }}px; color: rgba({{ scheme.text }}, 0.6); margin-top: 5px;">4.9/5 ★★★★★ (36 recensioni)</p>

<div class="carousel" id="reviewCarousel" style="
  --color-background: {{ scheme.background }};
  --color-foreground: {{ scheme.text }};
  --color-accent: {{ scheme.accent }};
">
  <button class="carousel-control-prev" onclick="prevSlide()">&#10094;</button>
  <div class="carousel-inner">
    <!-- Inizio recensioni -->
    <div class="carousel-track">
      <div class="carousel-item">
        <div class="review-card">
          <div class="reviewer-info">
            <h1 class="reviewer-name">Angela Reggidori</h1>
            <div class="review-stars">★★★★★</div>
            <div class="review-date"></div>
          </div>
          <div class="review-text">
              Esperienza super positiva. Ottimi prodotti, personale competente e disponibile.
          </div>
        </div>
      </div>
      <div class="carousel-item">
        <div class="review-card">
          <div class="reviewer-info">
            <h1 class="reviewer-name">Anna Simonetti</h1>
            <div class="review-stars">★★★★★</div>
            <div class="review-date"></div>
          </div>
          <div class="review-text">
              Emanuela ti segue con gentilezza e ti consiglia con cura. Scarpe comode e belle!
          </div>
        </div>
      </div>
      <div class="carousel-item">
        <div class="review-card">
          <div class="reviewer-info">
            <h1 class="reviewer-name">Chiara T.</h1>
            <div class="review-stars">★★★★☆</div>
            <div class="review-date"></div>
          </div>
          <div class="review-text">
              Servizio attento e cortese. Molto soddisfatta degli acquisti!
          </div>
        </div>
      </div>
    </div>
  </div>
  <button class="carousel-control-next" onclick="nextSlide()">&#10095;</button>

  <!-- Controlli mobile -->
  <div class="carousel-controls">
    <button class="carousel-control-prev" onclick="prevSlide()">&#10094;</button>
    <button class="carousel-control-next" onclick="nextSlide()">&#10095;</button>
  </div>
</div>
<style>
@media (max-width: 768px) {
  .carousel {
   width: 90% !important;
  }
}
  .carousel {
    width: 90%;
    max-width: 700px;
    overflow: hidden;
    position: relative;
    margin: 40px auto;
    border-radius: {{ settings.text_boxes_radius }} !important;
    box-shadow: 0 5px 20px rgba(0,0,0,0.1);
    background: rgb(var(--color-background));
    border: 1px solid rgba(var(--color-foreground), 0.1);
    font-family: var(--font-body-family);
    display: flex;
    align-items: center;
  }

  .carousel-inner {
    flex: 1;
    overflow: hidden;
  }

  .carousel-track {
    display: flex;
    transition: transform 0.5s ease;
  }

  .carousel-item {
    min-width: 100%;
    box-sizing: border-box;
    padding: 20px;
  }

  .review-card {
    display: flex;
    flex-direction: column;
    gap: 12px;
  }

  .reviewer-info {
    border-bottom: 1px solid rgba(var(--color-foreground), 0.1);
    padding-bottom: 10px;
  }

  .reviewer-name {
    font-size: var(--font-heading-scale);
    font-weight: 600;
    margin-bottom: 5px;
    color: rgb(var(--color-foreground));
    font-family: var(--font-heading-family);
  }

  .review-stars {
    color: #FFD700;
    font-size: var(--font-body-scale);
    margin-bottom: 3px;
  }

  .review-date {
    color: rgba(var(--color-foreground), 0.6);
    font-size: var(--font-body-scale);
  }

  .review-text {
    font-size: var(--font-body-scale);
    color: rgb(var(--color-foreground));
    line-height: 1.6;
    font-style: italic;
  }

  .carousel-control-prev,
  .carousel-control-next {
    position: relative;
    background-color: rgba(var(--color-accent), 0.8);
    color: rgb(var(--color-background));
    border: none;
    border-radius: 50%;
    width: 40px;
    height: 40px;
    cursor: pointer;
    font-size: 1.5rem;
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 2;
    transition: all 0.3s ease;
    flex-shrink: 0;
    margin: 0 10px;
  }

  .carousel-control-prev:hover,
  .carousel-control-next:hover {
    background-color: rgb(var(--color-accent));
    transform: scale(1.1);
  }

  .carousel-controls {
    display: none;
  }

  /* Responsive */
  @media (max-width: 768px) {
    .carousel {
      width: 95%;
      margin: 20px auto;
      flex-direction: column;
      gap: 15px;
      align-items: stretch;
    }

    .carousel-inner {
      order: 1;
      min-height: auto;
    }

    .carousel-item {
      padding: 20px 15px;
      min-height: auto;
    }

    .review-card {
      width: 100%;
    }

    .reviewer-name {
      font-size: var(--font-heading-scale);
      margin-bottom: 8px;
    }

    .review-stars {
      font-size: var(--font-body-scale);
    }

    .review-date {
      font-size: var(--font-body-scale);
    }

    .review-text {
      font-size: var(--font-body-scale);
      line-height: 1.6;
    }

    /* Nascondi controlli desktop */
    .carousel > .carousel-control-prev,
    .carousel > .carousel-control-next {
      display: none;
    }

    /* Mostra controlli mobile */
    .carousel-controls {
      order: 2;
      display: flex;
      justify-content: center;
      gap: 15px;
    }

    .carousel-controls .carousel-control-prev,
    .carousel-controls .carousel-control-next {
      position: static;
      width: 45px;
      height: 45px;
      font-size: 1.3rem;
      margin: 0;
      flex-shrink: 0;
      display: flex;
    }
  }

  @media (max-width: 480px) {
    .carousel {
      width: 98%;
      margin: 15px auto;
      gap: 12px;
    }

    .carousel-item {
      padding: 18px 12px;
    }

    .carousel-controls .carousel-control-prev,
    .carousel-controls .carousel-control-next {
      width: 40px;
      height: 40px;
      font-size: 1.2rem;
    }
  }
</style>

<script>
  let currentIndex = 0;

  function showSlide(index) {
    const track = document.querySelector('.carousel-track');
    const items = document.querySelectorAll('.carousel-item');
    const total = items.length;

    if (index >= total) currentIndex = 0;
    else if (index < 0) currentIndex = total - 1;
    else currentIndex = index;

    const offset = -currentIndex * 100;
    track.style.transform = `translateX(${offset}%)`;
  }

  function nextSlide() {
    showSlide(currentIndex + 1);
  }

  function prevSlide() {
    showSlide(currentIndex - 1);
  }

  // Auto-scroll ogni 5 secondi
  setInterval(nextSlide, 5000);

  // Inizializza il carousel
  document.addEventListener('DOMContentLoaded', function() {
    showSlide(0);
  });
</script>
